pragma solidity ^0.4.24;

import 'openzeppelin-solidity/contracts/token/ERC20/ERC20.sol';
import './Constant.sol';


contract ConstantWithLoan is Constant { 

        enum LoanState {Open, Closed, Liquidated}

        struct Collateral {
                uint assetAmount;
                uint assetPrice;
                uint threshold; // for automatic liquidation
        }

        struct Loan {
                uint principal;
                uint interest;
                uint since;
                Collateral collateral;
                address borrower;
                LoanState state;
        }

        Loan[] private loan;
        address private oracle;
        mapping(bytes32 => uint) private param; // network parameters such as ratio, threshold, interest
        mapping(bytes32 => uint) private data; // external data such as ethPrice

        event __borrow(uint lid, bytes32 offchain);
        event __payoff(bytes32 offchain);
        event __liquidate(bytes32 offchain);
        event __setParam(bytes32 offchain);
        event __setData(bytes32 offchain);

        constructor() public {
                oracle = msg.sender;
        }

        modifier onlyOracle() {
                require(msg.sender == oracle);
                _;
        }

        function updateOracle(address _oracle) public onlyAdmin {
                oracle = _oracle;
        }

        /**
         * @dev Function to extend a secured loan
         */
        function borrow(bytes32 offchain) public payable {
                Loan memory l;
                l.principal = (msg.value * data["ethPrice"] * param["ratio"]) / 100;
                l.interest = param["interest"];
                l.collateral = Collateral(msg.value, data["ethPrice"], param["threshold"]);
                l.since = now;
                l.borrower = msg.sender;
                l.state = LoanState.Open;

                loan.push(l);
                _mint(msg.sender, l.principal);

                emit __borrow(loan.length - 1, offchain); // catch this event
        }

        /**
         * @dev Function to pay off a secured loan
         * @param lid the loan id
         */
        function payoff(uint lid, bytes32 offchain) public {
                Loan storage l = loan[lid];

                require(l.state == LoanState.Open);
                require(msg.sender == l.borrower);

                l.state = LoanState.Closed;
                _transfer(msg.sender, this, l.principal * (1 + l.interest * (now - l.since)));
                _burn(this, l.principal);
                msg.sender.transfer(l.collateral.assetAmount);

                emit __payoff(offchain);
        }

        /**
         * @dev Function to handle automatic liquidation when the value of the collateral is low
         * @param lid the loan id
         */
        function liquidate(uint lid, bytes32 offchain) public onlyAdmin {
                Loan storage l = loan[lid];

                require(l.state == LoanState.Open);
                require(data["ethPrice"] < l.collateral.assetPrice * l.collateral.threshold);

                l.state = LoanState.Liquidated;
                admin.transfer(l.collateral.assetAmount);

                emit __liquidate(offchain);
        }

        /**
         * @dev Setter function to update a param value
         */
        function setParam(bytes32 key, uint value, bytes32 offchain) public onlyAdmin {
                param[key] = value;
                emit __setParam(offchain);
        }

        /**
         * @dev Getter function to get a param value
         */
        function getParam(bytes32 key) public view returns (uint){
                return param[key];
        }

        /**
         * @dev Setter function to update a data value
         */
        function setData(bytes32 key, uint value, bytes32 offchain) public onlyOracle {
                data[key] = value;
                emit __setData(offchain);
        }

        /**
         * @dev Getter function to get a data value
         */
        function getData(bytes32 key) public view returns (uint){
                return data[key];
        }
}
