pragma solidity ^0.4.24;

contract Delegation { 

        mapping(address => mapping(address => uint)) private delegated; 
        mapping(address => uint) internal sum; 

        event __delegate(bytes32 offchain);
        event __undelegate(bytes32 offchain);


        /**
         * @dev Delegate voting power to someone else
         */
        function _delegate(address to, uint value, bytes32 offchain) internal {
                delegated[msg.sender][to] += value;
                sum[to] += value;
                emit __delegate(offchain);
        }


        /**
         * @dev Delegate voting power to someone else
         */
        function _undelegate(address to, uint value, bytes32 offchain) internal {
                delegated[msg.sender][to] -= value;
                sum[to] -= value;
                emit __undelegate(offchain);
        }
}
